import { runInitiativeAndPublicTests, type SuiteResult } from '../__tests__/initiative_public_suite';

/**
 * Browser-compatible test runner for card effects
 * Can be called from browser console: window.__MILCH_TESTS__.run()
 */
export class CardEffectsTestRunner {
  private static instance: CardEffectsTestRunner;
  private results: SuiteResult | null = null;

  static getInstance(): CardEffectsTestRunner {
    if (!CardEffectsTestRunner.instance) {
      CardEffectsTestRunner.instance = new CardEffectsTestRunner();
    }
    return CardEffectsTestRunner.instance;
  }

  async runTests(): Promise<SuiteResult> {
    console.log('ðŸ§ª Starting Card Effects Test Suite...');
    this.results = await runInitiativeAndPublicTests();

    console.log('ðŸ“Š Test Results Summary:');
    console.table(this.results.summary);

    return this.results;
  }

  getResults(): SuiteResult | null {
    return this.results;
  }

  getFailedTests(): any[] {
    if (!this.results) return [];
    return this.results.items.filter(item => item.status === 'FAIL');
  }

  getPassedTests(): any[] {
    if (!this.results) return [];
    return this.results.items.filter(item => item.status === 'PASS');
  }

  getPendingTests(): any[] {
    if (!this.results) return [];
    return this.results.items.filter(item => item.status === 'PENDING');
  }

  getNoHandlerTests(): any[] {
    if (!this.results) return [];
    return this.results.items.filter(item => item.status === 'NO_HANDLER');
  }

  logDetailedResults(): void {
    if (!this.results) {
      console.log('âŒ No test results available. Run tests first.');
      return;
    }

    console.log('\nðŸ” DETAILED TEST RESULTS:');
    console.log('========================');

    this.results.items.forEach((item, index) => {
      const status = item.status === 'PASS' ? 'âœ…' :
                    item.status === 'FAIL' ? 'âŒ' :
                    item.status === 'PENDING' ? 'â³' : 'â“';

      console.log(`${index + 1}. ${status} ${item.cardName} (${item.effectKey || 'no key'})`);

      if (item.status === 'FAIL' && item.details) {
        console.log(`   Details:`, item.details);
      }
    });
  }

  exportResults(): void {
    if (!this.results) {
      console.log('âŒ No test results available. Run tests first.');
      return;
    }

    const data = JSON.stringify(this.results, null, 2);
    const blob = new Blob([data], { type: 'application/json' });
    const url = URL.createObjectURL(blob);

    const a = document.createElement('a');
    a.href = url;
    a.download = `card_effects_test_results_${new Date().toISOString().split('T')[0]}.json`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);

    console.log('ðŸ’¾ Test results exported to JSON file');
  }
}

// Browser global interface
declare global {
  interface Window {
    __MILCH_TESTS__?: {
      run: () => Promise<SuiteResult>;
      results: () => SuiteResult | null;
      failed: () => any[];
      passed: () => any[];
      pending: () => any[];
      noHandler: () => any[];
      details: () => void;
      export: () => void;
      runner: CardEffectsTestRunner;
    };
  }
}

// Initialize browser interface
if (typeof window !== 'undefined') {
  const runner = CardEffectsTestRunner.getInstance();

  window.__MILCH_TESTS__ = {
    run: () => runner.runTests(),
    results: () => runner.getResults(),
    failed: () => runner.getFailedTests(),
    passed: () => runner.getPassedTests(),
    pending: () => runner.getPendingTests(),
    noHandler: () => runner.getNoHandlerTests(),
    details: () => runner.logDetailedResults(),
    export: () => runner.exportResults(),
    runner
  };

  console.log('ðŸ§ª Card Effects Test Runner loaded. Use window.__MILCH_TESTS__.run() to start tests.');
}
