import React from 'react';
import { Icon, IconName } from './Icon';

// Map original icon names to manifest names
const ICON_MAPPING: Record<string, IconName> = {
  'government': 'government-row',
  'public': 'public-row',
  'initiative': 'initiative',
  'trap': 'intervention-trap',
  'ap': 'ap',
  'influence': 'influence',
  'round': 'round-turn',
  'buff': 'buff-strength',
  'draw': 'draw-cards',
  'discard': 'discard-cards',
  'return': 'return-to-hand',
  'deactivate': 'deactivate-card',
  'cancel': 'cancel-card',
  'shield': 'grant-shield',
  'aura': 'aura-ongoing',
  'science': 'aura-science',
  'health': 'aura-health',
  'military_penalty': 'aura-military-penalty',
  'register_trap': 'register-trap',
  'initiative_activated': 'initiative-activated',
  'start_of_turn': 'start-of-turn',
  'copy': 'copy-log',
  'clear': 'clear-log',
  'search': 'search',
  'money': 'budget-money',
  'log': 'game-log',
};

const DICT: Array<{ re: RegExp; icon: string }> = [
  { re: /\bGovernment\b/gi, icon: 'government' },
  { re: /\bPublic\b/gi, icon: 'public' },
  { re: /\bInitiative(s)?\b/gi, icon: 'initiative' },
  { re: /\bIntervention(s)?|Trap(s)?\b/gi, icon: 'trap' },
  { re: /\bAP\b/gi, icon: 'ap' },
  { re: /\bInfluence\b/gi, icon: 'influence' },
  { re: /\bRound(s)?\b/gi, icon: 'round' },
  { re: /\bBuff\b/gi, icon: 'buff' },
  { re: /\bDraw\b/gi, icon: 'draw' },
  { re: /\bDiscard\b/gi, icon: 'discard' },
  { re: /\bReturn to hand\b/gi, icon: 'return' },
  { re: /\bDeactivate\b/gi, icon: 'deactivate' },
  { re: /\bCancel\b/gi, icon: 'cancel' },
  { re: /\bShield\b/gi, icon: 'shield' },
  { re: /\bAura\b/gi, icon: 'aura' },
  { re: /\bScience\b/gi, icon: 'science' },
  { re: /\bHealth\b/gi, icon: 'health' },
  { re: /\bMilitary\b/gi, icon: 'military_penalty' },
  { re: /\bRegister Trap\b/gi, icon: 'register_trap' },
  { re: /\bInitiative Activated\b/gi, icon: 'initiative_activated' },
  { re: /\bStart of turn\b/gi, icon: 'start_of_turn' },
  { re: /\bCopy\b/gi, icon: 'copy' },
  { re: /\bClear\b/gi, icon: 'clear' },
  { re: /\bSearch\b/gi, icon: 'search' },
  { re: /\bBudget\b/gi, icon: 'money' },
  { re: /\bLog\b/gi, icon: 'log' },
];

export function withIcons(text: string, size = 14) {
  const parts: React.ReactNode[] = [text];
  DICT.forEach(({ re, icon }) => {
    for (let i = 0; i < parts.length; i++) {
      const chunk = parts[i];
      if (typeof chunk !== 'string') continue;
      const segs = chunk.split(re);
      if (segs.length === 1) continue;
      const matches = chunk.match(re) || [];
      const rebuilt: React.ReactNode[] = [];
      segs.forEach((s, idx) => {
        rebuilt.push(s);
        if (idx < matches.length) {
          const manifestIcon = ICON_MAPPING[icon];
          if (manifestIcon) {
            rebuilt.push(
              <span key={`${icon}-${i}-${idx}`} style={{ display: 'inline-flex', alignItems: 'center', gap: 4 }}>
                <Icon name={manifestIcon} size={size} />
                <span>{matches[idx]}</span>
              </span>
            );
          } else {
            // Fallback to text if icon not found
            rebuilt.push(<span key={`${icon}-${i}-${idx}`}>{matches[idx]}</span>);
          }
        }
      });
      parts.splice(i, 1, ...rebuilt);
      i += rebuilt.length - 1;
    }
  });
  return <>{parts}</>;
}
