              {/* Navigation Arrows */}
              <button
                onClick={handlePreviousCard}
                style={{
                  position: 'absolute',
                  left: '16px',
                  top: '50%',
                  transform: 'translateY(-50%)',
                  background: 'rgba(13, 22, 33, 0.9)',
                  border: '2px solid #1f3042',
                  borderRadius: '50%',
                  width: '48px',
                  height: '48px',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  cursor: 'pointer',
                  color: '#eaf3ff',
                  fontSize: '20px',
                  fontWeight: 'bold',
                  zIndex: 10,
                  transition: 'all 0.2s ease',
                }}
                onMouseEnter={(e) => {
                  e.currentTarget.style.background = 'rgba(22, 36, 54, 0.95)';
                  e.currentTarget.style.borderColor = '#27425b';
                }}
                onMouseLeave={(e) => {
                  e.currentTarget.style.background = 'rgba(13, 22, 33, 0.9)';
                  e.currentTarget.style.borderColor = '#1f3042';
                }}
              >
                â€¹
              </button>

              <button
                onClick={handleNextCard}
                style={{
                  position: 'absolute',
                  right: '16px',
                  top: '50%',
                  transform: 'translateY(-50%)',
                  background: 'rgba(13, 22, 33, 0.9)',
                  border: '2px solid #1f3042',
                  borderRadius: '50%',
                  width: '48px',
                  height: '48px',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  cursor: 'pointer',
                  color: '#eaf3ff',
                  fontSize: '20px',
                  fontWeight: 'bold',
                  zIndex: 10,
                  transition: 'all 0.2s ease',
                }}
                onMouseEnter={(e) => {
                  e.currentTarget.style.background = 'rgba(22, 36, 54, 0.95)';
                  e.currentTarget.style.borderColor = '#27425b';
                }}
                onMouseLeave={(e) => {
                  e.currentTarget.style.background = 'rgba(13, 22, 33, 0.9)';
                  e.currentTarget.style.borderColor = '#1f3042';
                }}
              >
                â€º
              </button>

              <canvas
                width={1024}
                height={1024}
                style={{
                  display: 'block',
                    width: '1024px',
                    height: '1024px',
                }}
                ref={(canvas) => {
                  if (canvas) {
                    const ctx = canvas.getContext('2d');
                    if (ctx) {
                      // Create a mock card object for drawing
                      const mockCard = {
                        kind: selectedCard.kind,
                        baseId: selectedCard.base.id,
                        name: selectedCard.base.name,
                        uid: selectedCard.base.id,
                        id: selectedCard.base.id,
                        key: selectedCard.base.key,
                      } as any;
                      drawCardImage(ctx, mockCard, 0, 0, 1024, 'modal');
                    }
                  }
                }}
              />
            </div>

              {/* Card Information Panel */}
            <div style={{
              flex: '1',
                minWidth: '400px',
              display: 'flex',
              flexDirection: 'column',
                gap: '20px',
                position: 'relative',
            }}>
                {/* Header with Cost in Top Right */}
              <div style={{
                display: 'flex',
                justifyContent: 'space-between',
                alignItems: 'flex-start',
              }}>
                  <div style={{ flex: 1 }}>
                  <h2 style={{
                      margin: '0 0 8px 0',
                      fontSize: '28px',
                    fontWeight: 700,
                    color: '#eaf3ff',
                      lineHeight: '1.2',
                  }}>
                    {selectedCard.base.name}
                  </h2>

                    {/* Category */}
                  <div style={{
                      fontSize: '16px',
                      fontWeight: 600,
                      color: '#dbe9f8',
                      marginBottom: '4px',
                    }}>
                      {cardDetails?.category || (selectedCard.kind === 'pol' ? 'Government' : 'Public')}
                    </div>

                    {/* Subcategories */}
                    {cardDetails?.subcategories && cardDetails.subcategories.length > 0 && (
                      <div style={{
                        fontSize: '14px',
                        color: '#a9c1da',
                        marginBottom: '4px',
                      }}>
                        {cardDetails.subcategories.join(', ')}
                      </div>
                    )}

                    {/* Position (without label) */}
                    {cardDetails?.highestPosition && (
                      <div style={{
                        fontSize: '14px',
                    color: '#8faecc',
                        marginBottom: '8px',
                      }}>
                        {cardDetails.highestPosition}
                      </div>
                    )}

                    {/* For specials: show type, tier, speed, slot */}
                    {selectedCard.kind === 'spec' && (
                      <div style={{
                        fontSize: '14px',
                        color: '#8faecc',
                        marginBottom: '4px',
                      }}>
                        {cardDetails?.cardType || (selectedCard.kind === 'spec' ? (selectedCard.base as any).type : '')}
                        {cardDetails?.tier && ` â€¢ ${cardDetails.tier}`}
                        {cardDetails?.speed && ` â€¢ ${cardDetails.speed}`}
                        {cardDetails?.slot && ` â€¢ Slot: ${cardDetails.slot}`}
                      </div>
                    )}

                    {/* For government: show base stats */}
                    {selectedCard.kind === 'pol' && (
                      <div style={{
                        display: 'flex',
                        gap: '10px',
                        flexWrap: 'wrap',
                        marginTop: '4px',
                        marginBottom: '4px',
                        color: '#a9c1da',
                        fontSize: '14px',
                      }}>
                        <span>Influence: {(selectedCard.base as any).influence}</span>
                        <span>Tier: {(selectedCard.base as any).T}</span>
                        <span>Keyword: {(selectedCard.base as any).tag}</span>
                      </div>
                    )}

                    <div style={{
                      fontSize: '12px',
                      color: '#6b7280',
                    fontWeight: 500,
                  }}>
                    Card {categorizedCards.allFilteredCards.findIndex(
                      card => card.kind === selectedCard.kind && card.base.id === selectedCard.base.id
                    ) + 1} of {categorizedCards.allFilteredCards.length}
                  </div>
                </div>

                  {/* Cost in Top Right Corner */}
                  <div style={{
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'flex-end',
                    gap: '8px',
                  }}>
                    <div style={{
                      background: '#1f2937',
                      border: '1px solid #374151',
                      borderRadius: '8px',
                      padding: '8px 12px',
                      fontSize: '14px',
                      color: '#e5e7eb',
                    }}>
                      Cost: {convertHPToUSD(cardDetails?.deckCost || (selectedCard.kind === 'pol' ? (selectedCard.base as BasePolitician).BP || 0 : (selectedCard.base as BaseSpecial).bp))}
                    </div>

                <button
                  onClick={handleCloseCardDetail}
                  style={{
                    background: 'none',
                    border: 'none',
                    color: '#8faecc',
                    fontSize: '24px',
                    cursor: 'pointer',
                    padding: '4px',
                    borderRadius: '4px',
                  }}
                  onMouseEnter={(e) => {
                    e.currentTarget.style.background = '#1a2a3a';
                  }}
                  onMouseLeave={(e) => {
                    e.currentTarget.style.background = 'none';
                  }}
                >
                  Ã—
                </button>
                  </div>
              </div>

                {/* Game Effect */}
              <div style={{
                  background: '#111827',
                  border: '1px solid #ffffff',
                  borderRadius: '12px',
                  padding: '20px',
              }}>
                <h3 style={{
                  margin: '0 0 12px 0',
                  fontSize: '16px',
                  fontWeight: 600,
                    color: '#e5e7eb',
                }}>
                    Game Effect
                </h3>

                  <p style={{
                    margin: 0,
                    color: '#d1d5db',
                    lineHeight: '1.5',
                    fontSize: '15px',
                  }}>
                    {cardDetails?.gameEffect || (selectedCard.kind === 'spec'
                      ? (selectedCard.base as BaseSpecial).effect
                      : 'Passive Politician abilities based on Tag')}
                  </p>
              </div>

                {/* Synergy for Public */}
                {cardDetails?.synergy && (
                  <div style={{
                    background: '#111827',
                    border: '1px solid #ffffff',
                    borderRadius: '12px',
                    padding: '20px',
                  }}>
                    <h3 style={{
                      margin: '0 0 12px 0',
                      fontSize: '16px',
                      fontWeight: 600,
                      color: '#e5e7eb',
                    }}>
                      Synergy
                    </h3>
                    <p style={{
                      margin: 0,
                      color: '#d1d5db',
                      lineHeight: '1.5',
                      fontSize: '15px',
                    }}>
                      {cardDetails.synergy}
                    </p>
                  </div>
                )}

                {/* Trigger Information for Interventions */}
                {cardDetails?.trigger && (
              <div style={{
                    background: '#111827',
                    border: '1px solid #ffffff',
                    borderRadius: '12px',
                    padding: '20px',
              }}>
                <h3 style={{
                  margin: '0 0 12px 0',
                  fontSize: '16px',
                  fontWeight: 600,
                      color: '#e5e7eb',
                }}>
                      Trigger
                </h3>
                <p style={{
                  margin: 0,
                      color: '#d1d5db',
                  lineHeight: '1.5',
                      fontSize: '15px',
                }}>
                      {cardDetails.trigger}
                </p>
              </div>
                )}

                {/* Usage and Example for Initiatives/Interventions */}
                {(cardDetails?.usage || cardDetails?.example) && (
                  <div style={{
                    background: '#111827',
                    border: '1px solid #ffffff',
                    borderRadius: '12px',
                    padding: '20px',
                  }}>
                    {cardDetails.usage && (
                      <div style={{ marginBottom: '10px' }}>
                        <div style={{ fontSize: '12px', color: '#9ca3af', marginBottom: '4px' }}>When to play?</div>
                        <div style={{ color: '#d1d5db', fontSize: '15px', lineHeight: '1.5' }}>{cardDetails.usage}</div>
                      </div>
                    )}
                    {cardDetails.example && (
                      <div>
                        <div style={{ fontSize: '12px', color: '#9ca3af', marginBottom: '4px' }}>Example</div>
                        <div style={{ color: '#d1d5db', fontSize: '15px', lineHeight: '1.5' }}>{cardDetails.example}</div>
                      </div>
                    )}
                  </div>
                )}

                {/* Personal Information */}
                {cardDetails && (cardDetails.nationality || cardDetails.birthDate || cardDetails.estimatedWealth || cardDetails.controversialQuote) && (
                  <div style={{
                    background: '#111827',
                    border: '1px solid #ffffff',
                    borderRadius: '12px',
                    padding: '20px',
                  }}>
                    <h3 style={{
                      margin: '0 0 16px 0',
                      fontSize: '16px',
                      fontWeight: 600,
                      color: '#e5e7eb',
                    }}>
                      Personal Information
                    </h3>

                    <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '12px' }}>
                      {cardDetails.nationality && (
                        <div>
                          <div style={{ fontSize: '12px', color: '#9ca3af', marginBottom: '4px' }}>Nationality</div>
                          <div style={{ color: '#e5e7eb', fontWeight: 500 }}>{cardDetails.nationality}</div>
                        </div>
                      )}

                      {cardDetails.birthDate && (
                        <div>
                          <div style={{ fontSize: '12px', color: '#9ca3af', marginBottom: '4px' }}>Birth Date</div>
                          <div style={{ color: '#e5e7eb', fontWeight: 500 }}>{cardDetails.birthDate}</div>
                        </div>
                      )}
                    </div>

                    {cardDetails.estimatedWealth && (
                      <div style={{ marginTop: '12px' }}>
                        <div style={{ fontSize: '12px', color: '#9ca3af', marginBottom: '4px' }}>Estimated Wealth</div>
                        <div style={{ color: '#e5e7eb', fontWeight: 500 }}>{formatWealth(cardDetails.estimatedWealth)}</div>
                      </div>
                    )}

                    {cardDetails.controversialQuote && (
                      <div style={{
                        marginTop: '16px',
                        background: '#1f2937',
                        border: '1px solid #ffffff',
                        borderRadius: '8px',
                        padding: '12px',
                      }}>
                        <div style={{ fontSize: '12px', color: '#9ca3af', marginBottom: '6px' }}>Quote</div>
                        <div style={{
                          color: '#d1d5db',
                          fontStyle: 'italic',
                          lineHeight: '1.4',
                          fontSize: '14px',
                        }}>
                          {cardDetails.controversialQuote}
                        </div>
                      </div>
                    )}

                    {/* Sources integrated into modal */}
                    {cardDetails?.sources && cardDetails.sources.length > 0 && (
                      <div style={{ marginTop: '16px' }}>
                        <button
                          onClick={() => setShowSources(!showSources)}
                          style={{
                            background: 'none',
                            border: 'none',
                            color: '#9ca3af',
                            fontSize: '12px',
                            cursor: 'pointer',
                            textDecoration: 'underline',
                            padding: 0,
                          }}
                        >
                          Show Sources
                        </button>
                      </div>
                    )}
                  </div>
                )}

                {/* Action Button */}
              <div style={{
                display: 'flex',
                gap: '12px',
                marginTop: 'auto',
              }}>
                <button
                  onClick={() => {
                    builderAdd(selectedCard.base, selectedCard.kind);
                    handleCloseCardDetail();
                  }}
                  disabled={!builderCanAdd(selectedCard.base, selectedCard.kind)}
                  style={{
                    flex: 1,
                      background: builderCanAdd(selectedCard.base, selectedCard.kind)
                        ? '#374151'
                        : '#1f2937',
                      color: '#e5e7eb',
                      border: '1px solid #4b5563',
                    borderRadius: '8px',
                    padding: '12px',
                    fontSize: '14px',
                    fontWeight: 600,
                      cursor: builderCanAdd(selectedCard.base, selectedCard.kind) ? 'pointer' : 'not-allowed',
                      opacity: builderCanAdd(selectedCard.base, selectedCard.kind) ? 1 : 0.6,
                  }}
                >
                  Add to Deck
                </button>
              </div>
            </div>
            </div>

            {/* Mini Modal for Sources */}
            {showSources && cardDetails?.sources && (
              <div style={{
                position: 'fixed',
                top: 'calc(50% + 5px)',
                left: 'calc(50% + 5px)',
                transform: 'translate(-50%, -50%)',
                background: '#0d1621',
                border: '2px solid #ffffff',
                borderRadius: '12px',
                padding: '20px',
                minWidth: '400px',
                maxWidth: '600px',
                boxShadow: '0 20px 60px rgba(0, 0, 0, 0.8)',
                zIndex: 60,
              }}>
                <div style={{
                  display: 'flex',
                  justifyContent: 'space-between',
                  alignItems: 'center',
                  marginBottom: '16px',
                }}>
                  <h4 style={{
                    margin: 0,
                    fontSize: '16px',
                    fontWeight: 600,
                    color: '#e5e7eb',
                  }}>
                    Sources & Calculation Basis
                  </h4>
                  <button
                    onClick={() => setShowSources(false)}
                    style={{
                      background: 'none',
                      border: 'none',
                      color: '#9ca3af',
                      fontSize: '20px',
                      cursor: 'pointer',
                      padding: '4px',
                    }}
                  >
                    Ã—
                  </button>
                </div>

                {cardDetails.calculation && (
                  <div style={{ marginBottom: '16px' }}>
                    <div style={{ fontSize: '12px', color: '#9ca3af', marginBottom: '6px' }}>Calculation:</div>
                    <div style={{
                      color: '#d1d5db',
                      fontSize: '13px',
                      lineHeight: '1.4',
                      background: '#1f2937',
                      padding: '10px',
                      borderRadius: '6px',
                    }}>
                      {cardDetails.calculation}
          </div>
        </div>
      )}

                <div style={{ fontSize: '12px', color: '#9ca3af', marginBottom: '6px' }}>Sources:</div>
                <div style={{
                  color: '#d1d5db',
                  fontSize: '13px',
                  lineHeight: '1.4',
                }}>
                  {formatSources(cardDetails.sources)}
                </div>
              </div>
            )}
          </div>
        );
      })()}
    </div>
  );
};
