import React, { useEffect, useRef } from 'react';
// Note: pixi.js must be installed in the project: `npm i pixi.js`
let PIXI: any;
try { PIXI = require('pixi.js'); } catch (e) { PIXI = null; }
// Try to require webm asset at runtime; fallback to static path if not resolvable
let slotGovWebm: string;
try {
  // eslint-disable-next-line @typescript-eslint/no-var-requires
  slotGovWebm = require('../ui/layout/slot_gov.webm');
} catch (e) {
  slotGovWebm = '/static/media/slot_gov.webm';
}

type SpawnOpts = { id?: string|number; cx: number; cy: number; w: number; h: number; src: string; duration?: number };

const PixiOverlay: React.FC = () => {
  const containerRef = useRef<HTMLDivElement | null>(null);
  const appRef = useRef<any | null>(null);

  useEffect(() => {
    const div = containerRef.current;
    if (!div) return;

    if (!PIXI) {
      console.warn('PixiOverlay: PIXI not available in this runtime; skipping Pixi overlay initialization');
      return;
    }

    const app = new PIXI.Application({
      width: window.innerWidth,
      height: window.innerHeight,
      transparent: true,
      antialias: true,
      resolution: typeof window !== 'undefined' ? window.devicePixelRatio || 1 : 1,
    });

    app.view.style.position = 'fixed';
    app.view.style.left = '0px';
    app.view.style.top = '0px';
    app.view.style.pointerEvents = 'none';
    app.view.style.zIndex = '900';
    appRef.current = app;
    div.appendChild(app.view as HTMLCanvasElement);

    const resize = () => {
      app.renderer.resize(window.innerWidth, window.innerHeight);
    };
    window.addEventListener('resize', resize);

    // Expose simple spawn API
    (window as any).__pc_pixi = (window as any).__pc_pixi || {};
    const videoCache = new Map<string, HTMLVideoElement>();
    // Preload gov slot video once and reuse
    try {
      const govVideo = document.createElement('video');
      govVideo.src = slotGovWebm as string;
      govVideo.muted = true;
      govVideo.playsInline = true;
      govVideo.preload = 'auto';
      govVideo.crossOrigin = 'anonymous';
      govVideo.style.display = 'none';
      document.body.appendChild(govVideo);
      videoCache.set(slotGovWebm as string, govVideo);
      // attempt to load metadata for duration
      govVideo.load();
    } catch (e) {
      console.warn('PixiOverlay: preload gov webm failed', e);
    }
    (window as any).__pc_pixi.spawnSlotEffect = (opts: SpawnOpts) => {
      try {
        const playVideoInstance = (video: HTMLVideoElement) => {
          const tex = PIXI.Texture.from(video as any);
          const sprite = new PIXI.Sprite(tex);
          sprite.anchor.set(0.5, 0.5);
          sprite.x = opts.cx;
          sprite.y = opts.cy;
          sprite.width = opts.w;
          sprite.height = opts.h;
          sprite.alpha = 0;
          sprite.scale.set(0.9);
          app.stage.addChild(sprite);

          const start = performance.now();
          const dur = opts.duration ?? (video.duration ? Math.round(video.duration * 1000) : 700);
          const tick = () => {
            const now = performance.now();
            const t = Math.min(1, (now - start) / dur);
            sprite.alpha = Math.sin((t * Math.PI) / 2);
            sprite.scale.set(0.9 + 0.2 * Math.sin((t * Math.PI) / 2));
            if (t < 1) requestAnimationFrame(tick);
            else {
              const fadeStart = performance.now();
              const fadeDur = 120;
              const fade = () => {
                const now2 = performance.now();
                const tf = Math.min(1, (now2 - fadeStart) / fadeDur);
                sprite.alpha = Math.max(0, 1 - tf);
                if (tf < 1) requestAnimationFrame(fade);
                else {
                  try { app.stage.removeChild(sprite); sprite.destroy({ texture: true, baseTexture: true }); } catch(e){}
                }
              };
              requestAnimationFrame(fade);
            }
          };
          requestAnimationFrame(tick);
        };

        const src = opts.src;
        // Prefer cached video element to avoid re-decoding
        let video = videoCache.get(src);
        if (!video) {
          video = document.createElement('video');
          video.src = src;
          video.muted = true;
          video.autoplay = false;
          video.playsInline = true;
          video.crossOrigin = 'anonymous';
          video.preload = 'auto';
          video.style.display = 'none';
          document.body.appendChild(video);
          videoCache.set(src, video);
        }

        // Play from start
        video.currentTime = 0;
        const playPromise = video?.play?.();
        if (playPromise && typeof playPromise.then === 'function') {
          playPromise.then(() => {
            if (video) playVideoInstance(video);
          }).catch(() => {
            // If play blocked, still try to use texture when loaded
            if (video && video.readyState >= 2) playVideoInstance(video);
            else if (video) {
              const onload = function onload() { video && video.removeEventListener('loadeddata', onload); video && playVideoInstance(video); };
              video.addEventListener('loadeddata', onload);
            }
          });
        } else {
          if (video && video.readyState >= 2) playVideoInstance(video);
          else if (video) {
            const onload = function onload() { video && video.removeEventListener('loadeddata', onload); video && playVideoInstance(video); };
            video.addEventListener('loadeddata', onload);
          }
        }

      } catch (e) {
        console.warn('Pixi spawn failed', e, opts);
      }
    };

    return () => {
      try { window.removeEventListener('resize', resize); } catch(e){}
      try { (window as any).__pc_pixi = undefined; } catch(e){}
      try { app.destroy(true, { children: true }); } catch(e){}
      if (div && app.view && div.contains(app.view as any)) div.removeChild(app.view as any);
    };
  }, []);

  return <div ref={containerRef} style={{ position: 'fixed', left: 0, top: 0, right: 0, bottom: 0, pointerEvents: 'none', zIndex: 900 }} />;
};

export default PixiOverlay;
