import { GameState, Player, Card } from '../types/game';
import { EffectEvent } from '../types/effects';
import { resolveQueue } from '../utils/queue';
import { getStrongestGovernmentUid } from '../utils/targets';
import { triggerCardEffect } from './registry';

// Legacy German ‚Üî effectKey mapping (fallback until all cards get effectKey directly)
export const LEGACY_NAME_TO_KEY: Record<string, string> = {
  'Bill Gates': 'public.bill_gates.next_initiative_ap1',
  // --- Instant Initiatives ---
  'Partei-Offensive':        'init.party_offensive.deactivate_gov',
  'Oppositionsblockade':     'init.opposition_blockade.lock_initiatives',
  'Verz√∂gerungsverfahren':   'init.delay_tactics.ap_or_draw',
  'Think-tank':              'init.think_tank.draw1_buff_gov2',
  'Influencer-Kampagne':     'init.influencer_campaign.double_public',
  'Systemrelevant':          'init.system_critical.shield1',
  'Symbolpolitik':           'init.symbolic_politics.draw1',
  'Opportunist':             'init.opportunist.mirror_ap_effects',
  // vorhanden
  'Digitaler Wahlkampf':     'init.digital_campaign.draw2',

  // --- Traps ---
  'Interne Fraktionsk√§mpfe': 'trap.internal_faction_strife.cancel_big_initiative',
  'Boykott-Kampagne':        'trap.boycott.deactivate_ngo_movement',
  'Deepfake-Skandal':        'trap.deepfake.lock_diplomat_transfer',
  'Cyber-Attacke':           'trap.cyber_attack.destroy_platform',
  'Bestechungsskandal 2.0':  'trap.bribery_v2.mind_control_weak_gov',
  'Grassroots-Widerstand':   'trap.grassroots_resistance.deactivate_public',
  'Massenproteste':          'trap.mass_protests.debuff_two_govs',
  'Berater-Aff√§re':          'trap.advisor_scandal.minus2_gov_tier1',
  'Parlament geschlossen':   'trap.parliament_closed.stop_more_gov',
  '"Unabh√§ngige" Untersuchung': 'trap.independent_investigation.cancel_trap',
  'Soft Power-Kollaps':      'trap.soft_power_collapse.minus3_diplomat',
  'Cancel Culture':          'trap.cancel_culture.deactivate_public',
  'Lobby Leak':              'trap.lobby_leak.force_discard_on_ngo',
  'Maulwurf':                'trap.mole.copy_weaker_gov',
  'Skandalspirale':          'trap.scandal_spiral.cancel_one_of_two',
  'Tunnelvision':            'trap.tunnel_vision.ignore_weak_gov',
  'Satire-Show':             'trap.satire_show.minus2_enemy_gov',
  'Strategische Enth√ºllung': 'trap.strategic_disclosure.return_gov',
  // vorhanden
  'Fake News-Kampagne':      'trap.fake_news.deactivate_media',
};

function other(p: Player): Player { return p === 1 ? 2 : 1; }

export function triggerCardEffects(state: GameState, player: Player, card: Card) {
  // Try registry first (new system)
  if (LEGACY_NAME_TO_KEY[card.name]) {
    triggerCardEffect(state, player, card);
    return;
  }

  // Fallback to legacy switch statement for unmapped cards
  // Ensure effectQueue exists (migrated from _queue)
  if (!(state as any)._effectQueue) (state as any)._effectQueue = [];

  switch (card.name) {
    // --- Public (Oligarch/Plattform/NGO/Bewegung etc.) ---
    case 'Elon Musk': {
      (state as any)._effectQueue.push({ type: 'DRAW_CARDS', player, amount: 1 });
      (state as any)._effectQueue.push({ type: 'ADD_AP', player, amount: 1 }); // Simplified: direct AP instead of discount
      (state as any)._effectQueue.push({ type: 'LOG', msg: 'Elon Musk: +1 Karte, +1 AP' });
      break;
    }
    case 'Bill Gates': {
      (state as any)._effectQueue.push({ type: 'DRAW_CARDS', player, amount: 1 });
      (state as any)._effectQueue.push({ type: 'ADD_AP', player, amount: 1 });
      (state as any)._effectQueue.push({ type: 'LOG', msg: 'Bill Gates: +1 Karte, +1 AP' });
      break;
    }
    case 'Mark Zuckerberg': {
      (state as any)._effectQueue.push({ type: 'ADD_AP', player, amount: 1 }); // Simplified: direct AP instead of refund
      (state as any)._effectQueue.push({ type: 'LOG', msg: 'Mark Zuckerberg: +1 AP' });
      break;
    }
    case 'Jack Ma': {
      (state as any)._effectQueue.push({ type: 'DRAW_CARDS', player, amount: 1 });
      (state as any)._effectQueue.push({ type: 'LOG', msg: 'Jack Ma: +1 Karte' });
      break;
    }
    case 'Zhang Yiming': {
      (state as any)._effectQueue.push({ type: 'ADD_AP', player, amount: 1 });
      (state as any)._effectQueue.push({ type: 'LOG', msg: 'Zhang Yiming: +1 AP' });
      break;
    }
    case 'Mukesh Ambani': {
      (state as any)._effectQueue.push({ type: 'ADD_AP', player, amount: 1 });
      (state as any)._effectQueue.push({ type: 'LOG', msg: 'Mukesh Ambani: +1 AP' });
      break;
    }
    case 'Roman Abramovich': {
      (state as any)._effectQueue.push({ type: 'ADD_AP', player, amount: 1 });
      (state as any)._effectQueue.push({ type: 'LOG', msg: 'Roman Abramovich: +1 AP' });
      break;
    }
    case 'Alisher Usmanov': {
      (state as any)._effectQueue.push({ type: 'DRAW_CARDS', player, amount: 1 });
      (state as any)._effectQueue.push({ type: 'LOG', msg: 'Alisher Usmanov: +1 Karte' });
      break;
    }
    case 'Oprah Winfrey': {
      // je 1 zuf√§llige Handkarte bei beiden deaktivieren (als abgeworfen interpretieren)
      (state as any)._effectQueue.push({ type: 'DEACTIVATE_RANDOM_HAND', player, amount: 1 });
      (state as any)._effectQueue.push({ type: 'DEACTIVATE_RANDOM_HAND', player: other(player), amount: 1 });
      (state as any)._effectQueue.push({ type: 'LOG', msg: 'Oprah Winfrey: jeweils 1 zuf√§llige Handkarte beider Spieler deaktiviert' });
      break;
    }
    case 'George Soros': {
      (state as any)._effectQueue.push({ type: 'ADD_AP', player, amount: 1 });
      (state as any)._effectQueue.push({ type: 'LOG', msg: 'George Soros: +1 AP' });
      break;
    }

    // --- Neue Karten gem√§√ü Guidelines ¬ß9 ---
    case 'Warren Buffett': {
      (state as any)._effectQueue.push({ type: 'DRAW_CARDS', player, amount: 2 });
      (state as any)._effectQueue.push({ type: 'ADD_AP', player, amount: 1 });
      (state as any)._effectQueue.push({ type: 'LOG', msg: 'Warren Buffett: +2 Karten, +1 AP' });
      break;
    }
    case 'Jeff Bezos': {
      (state as any)._effectQueue.push({ type: 'ADD_AP', player, amount: 2 });
      (state as any)._effectQueue.push({ type: 'LOG', msg: 'Jeff Bezos: +2 AP' });
      break;
    }
    case 'Larry Page': {
      (state as any)._effectQueue.push({ type: 'DRAW_CARDS', player, amount: 1 });
      (state as any)._effectQueue.push({ type: 'ADD_AP', player, amount: 1 });
      (state as any)._effectQueue.push({ type: 'LOG', msg: 'Larry Page: +1 Karte, +1 AP' });
      break;
    }
    case 'Sergey Brin': {
      (state as any)._effectQueue.push({ type: 'DRAW_CARDS', player, amount: 1 });
      (state as any)._effectQueue.push({ type: 'ADD_AP', player, amount: 1 });
      (state as any)._effectQueue.push({ type: 'LOG', msg: 'Sergey Brin: +1 Karte, +1 AP' });
      break;
    }
    case 'Tim Cook': {
      (state as any)._effectQueue.push({ type: 'ADD_AP', player, amount: 2 });
      (state as any)._effectQueue.push({ type: 'LOG', msg: 'Tim Cook: +2 AP' });
      break;
    }

    // --- Government (Leadership/Diplomat etc. vereinfachte Sofort-Effekte bei Play) ---
    case 'Vladimir Putin': {
      (state as any)._effectQueue.push({ type: 'BUFF_STRONGEST_GOV', player, amount: 1 });
      (state as any)._effectQueue.push({ type: 'LOG', msg: 'Vladimir Putin: st√§rkste Regierung +1 Einfluss' });
      break;
    }
    case 'Xi Jinping': {
      (state as any)._effectQueue.push({ type: 'BUFF_STRONGEST_GOV', player, amount: 1 });
      (state as any)._effectQueue.push({ type: 'LOG', msg: 'Xi Jinping: st√§rkste Regierung +1 Einfluss' });
      break;
    }
    case 'Recep Tayyip Erdoƒüan':
    case 'Recep Tayyip Erdogan':
    case 'Erdogan': {
      (state as any)._effectQueue.push({ type: 'DISCARD_RANDOM_FROM_HAND', player: other(player), amount: 1 });
      (state as any)._effectQueue.push({ type: 'LOG', msg: 'Erdoƒüan: Gegner wirft 1 zuf√§llige Handkarte ab' });
      break;
    }
    case 'Joschka Fischer': {
      (state as any)._effectQueue.push({ type: 'DRAW_CARDS', player, amount: 1 });
      (state as any)._effectQueue.push({ type: 'LOG', msg: 'Joschka Fischer: +1 Karte' });
      break;
    }
          case 'Ursula von der Leyen': {
        const uid = getStrongestGovernmentUid(state, player);
        if (uid != null) {
          (state as any)._effectQueue.push({ type: 'GRANT_SHIELD', player, targetUid: uid, amount: 1 });
        }
        (state as any)._effectQueue.push({ type: 'LOG', msg: 'Ursula von der Leyen: üõ°Ô∏è Schutz auf st√§rkste Regierung' });
        break;
      }
    case 'Emmanuel Macron': {
      (state as any)._effectQueue.push({ type: 'ADD_AP', player, amount: 1 });
      (state as any)._effectQueue.push({ type: 'LOG', msg: 'Emmanuel Macron: +1 AP' });
      break;
    }
    case 'Olaf Scholz': {
      (state as any)._effectQueue.push({ type: 'DRAW_CARDS', player, amount: 1 });
      (state as any)._effectQueue.push({ type: 'LOG', msg: 'Olaf Scholz: +1 Karte' });
      break;
    }
    case 'Luiz In√°cio Lula da Silva':
    case 'Luiz Inacio Lula da Silva': {
      (state as any)._effectQueue.push({ type: 'BUFF_STRONGEST_GOV', player, amount: 1 });
      (state as any)._effectQueue.push({ type: 'LOG', msg: 'Luiz In√°cio Lula da Silva: st√§rkste Regierung +1 Einfluss' });
      break;
    }
          case 'Volodymyr Zelenskyy': {
        const uid = getStrongestGovernmentUid(state, player);
        if (uid != null) {
          (state as any)._effectQueue.push({ type: 'GRANT_SHIELD', player, targetUid: uid, amount: 1 });
        }
        (state as any)._effectQueue.push({ type: 'LOG', msg: 'Volodymyr Zelenskyy: üõ°Ô∏è Schutz auf st√§rkste Regierung' });
        break;
      }
    case 'Sergey Lavrov': {
      const opp: Player = player === 1 ? 2 : 1;
      (state as any)._effectQueue.push({ type: 'DISCARD_RANDOM_FROM_HAND', player: opp, amount: 1 });
      (state as any)._effectQueue.push({ type: 'LOG', msg: 'Sergey Lavrov: Gegner wirft 1 zuf√§llige Handkarte ab' });
      break;
    }

    // --- Neue Government-Karten ---
    case 'Angela Merkel': {
      (state as any)._effectQueue.push({ type: 'DRAW_CARDS', player, amount: 2 });
      (state as any)._effectQueue.push({ type: 'LOG', msg: 'Angela Merkel: +2 Karten' });
      break;
    }
    case 'Joe Biden': {
      (state as any)._effectQueue.push({ type: 'ADD_AP', player, amount: 1 });
      (state as any)._effectQueue.push({ type: 'DRAW_CARDS', player, amount: 1 });
      (state as any)._effectQueue.push({ type: 'LOG', msg: 'Joe Biden: +1 AP, +1 Karte' });
      break;
    }
    case 'Justin Trudeau': {
      (state as any)._effectQueue.push({ type: 'BUFF_STRONGEST_GOV', player, amount: 2 });
      (state as any)._effectQueue.push({ type: 'LOG', msg: 'Justin Trudeau: st√§rkste Regierung +2 Einfluss' });
      break;
    }
    case 'Shinzo Abe': {
      (state as any)._effectQueue.push({ type: 'DRAW_CARDS', player, amount: 1 });
      (state as any)._effectQueue.push({ type: 'ADD_AP', player, amount: 1 });
      (state as any)._effectQueue.push({ type: 'LOG', msg: 'Shinzo Abe: +1 Karte, +1 AP' });
      break;
    }
    case 'Narendra Modi': {
      (state as any)._effectQueue.push({ type: 'ADD_AP', player, amount: 1 });
      (state as any)._effectQueue.push({ type: 'BUFF_STRONGEST_GOV', player, amount: 1 });
      (state as any)._effectQueue.push({ type: 'LOG', msg: 'Narendra Modi: +1 AP, st√§rkste Regierung +1 Einfluss' });
      break;
    }

    // --- N√§chste 10 Karten gem√§√ü Guidelines ¬ß9 ---
    case 'Sam Altman': {
      (state as any)._effectQueue.push({ type: 'DRAW_CARDS', player, amount: 1 });
      (state as any)._effectQueue.push({ type: 'ADD_AP', player, amount: 1 });
      (state as any)._effectQueue.push({ type: 'LOG', msg: 'Sam Altman: +1 Karte, +1 AP' });
      break;
    }
    case 'Greta Thunberg': {
      (state as any)._effectQueue.push({ type: 'DISCARD_RANDOM_FROM_HAND', player: other(player), amount: 1 });
      (state as any)._effectQueue.push({ type: 'LOG', msg: 'Greta Thunberg: Gegner wirft 1 zuf√§llige Handkarte ab' });
      break;
    }
    case 'Jennifer Doudna': {
      (state as any)._effectQueue.push({ type: 'DRAW_CARDS', player, amount: 1 });
      (state as any)._effectQueue.push({ type: 'ADD_AP', player, amount: 1 });
      (state as any)._effectQueue.push({ type: 'LOG', msg: 'Jennifer Doudna: +1 Karte, +1 AP' });
      break;
    }
    case 'Malala Yousafzai': {
      (state as any)._effectQueue.push({ type: 'DRAW_CARDS', player, amount: 1 });
      (state as any)._effectQueue.push({ type: 'LOG', msg: 'Malala Yousafzai: +1 Karte' });
      break;
    }
    case 'Noam Chomsky': {
      (state as any)._effectQueue.push({ type: 'ADD_AP', player, amount: 1 });
      (state as any)._effectQueue.push({ type: 'LOG', msg: 'Noam Chomsky: +1 AP' });
      break;
    }
    case 'Ai Weiwei': {
      (state as any)._effectQueue.push({ type: 'DISCARD_RANDOM_FROM_HAND', player: other(player), amount: 1 });
      (state as any)._effectQueue.push({ type: 'LOG', msg: 'Ai Weiwei: Gegner wirft 1 zuf√§llige Handkarte ab' });
      break;
    }
    case 'Alexei Navalny': {
      (state as any)._effectQueue.push({ type: 'DRAW_CARDS', player, amount: 1 });
      (state as any)._effectQueue.push({ type: 'BUFF_STRONGEST_GOV', player, amount: 1 });
      (state as any)._effectQueue.push({ type: 'LOG', msg: 'Alexei Navalny: +1 Karte, st√§rkste Regierung +1 Einfluss' });
      break;
    }
    case 'Anthony Fauci': {
      (state as any)._effectQueue.push({ type: 'DRAW_CARDS', player, amount: 1 });
      (state as any)._effectQueue.push({ type: 'ADD_AP', player, amount: 1 });
      (state as any)._effectQueue.push({ type: 'LOG', msg: 'Anthony Fauci: +1 Karte, +1 AP' });
      break;
    }
    case 'Gautam Adani': {
      (state as any)._effectQueue.push({ type: 'ADD_AP', player, amount: 1 });
      (state as any)._effectQueue.push({ type: 'DRAW_CARDS', player, amount: 1 });
      (state as any)._effectQueue.push({ type: 'LOG', msg: 'Gautam Adani: +1 AP, +1 Karte' });
      break;
    }
    case 'Edward Snowden': {
      (state as any)._effectQueue.push({ type: 'DISCARD_RANDOM_FROM_HAND', player: other(player), amount: 1 });
      (state as any)._effectQueue.push({ type: 'LOG', msg: 'Edward Snowden: Gegner wirft 1 zuf√§llige Handkarte ab' });
      break;
    }

    // --- Sofort-Initiativen (Beispiele, wenn du sie direkt aktivierst) ---
    case 'Digitaler Wahlkampf': {
      (state as any)._effectQueue.push({ type: 'DRAW_CARDS', player, amount: 2 });
      (state as any)._effectQueue.push({ type: 'ADD_AP', player, amount: 1 }); // Simplified: direct AP instead of discount
      (state as any)._effectQueue.push({ type: 'LOG', msg: 'Digitaler Wahlkampf: +2 Karten, +1 AP' });
      break;
    }
    case 'Verz√∂gerungsverfahren':
    case 'Verzoegerungsverfahren': {
      (state as any)._effectQueue.push({ type: 'ADD_AP', player, amount: 1 });
      (state as any)._effectQueue.push({ type: 'LOG', msg: 'Verz√∂gerungsverfahren: +1 AP' });
      break;
    }
    case 'Spin Doctor': {
      (state as any)._effectQueue.push({ type: 'BUFF_STRONGEST_GOV', player, amount: 1 });
      (state as any)._effectQueue.push({ type: 'LOG', msg: 'Spin Doctor: st√§rkste Regierung +1 Einfluss' });
      break;
    }

    default:
      // Kein Effekt ‚Äì bewusst still
      break;
  }
}
