import React from 'react';
import iconManifest from '../assets/icons/manifest.json';

// Dynamic icon imports based on manifest
const iconImports: Record<string, React.ComponentType<React.SVGProps<SVGSVGElement>>> = {};

// Import all icons dynamically
Object.keys(iconManifest).forEach(iconName => {
  try {
    // Convert kebab-case to camelCase for import
    const importName = iconName.replace(/-([a-z])/g, (_, letter) => letter.toUpperCase());
    const iconModule = require(`../assets/icons/${iconName}.svg`);
    iconImports[iconName] = iconModule.ReactComponent || iconModule.default;
  } catch (error) {
    console.warn(`Failed to import icon: ${iconName}`, error);
  }
});

// Fallback component for missing icons
const IconFallback: React.FC<React.SVGProps<SVGSVGElement>> = ({ width = 16, height = 16, ...props }) => (
  <svg 
    width={width} 
    height={height} 
    viewBox="0 0 24 24" 
    fill="currentColor"
    {...props}
  >
    <rect width="24" height="24" fill="none" stroke="currentColor" strokeWidth="2" opacity="0.3"/>
    <text x="12" y="16" textAnchor="middle" fontSize="12" fill="currentColor">?</text>
  </svg>
);

// Type-safe icon names
export type IconName = keyof typeof iconManifest;

export function Icon({
  name,
  size = 16,
  className,
  title,
  ...rest
}: {
  name: IconName;
  size?: number;
  className?: string;
  title?: string;
} & React.SVGProps<SVGSVGElement>) {
  const IconComponent = iconImports[name] || IconFallback;
  
  return (
    <IconComponent 
      width={size} 
      height={size} 
      aria-label={title || name} 
      className={className} 
      {...rest} 
    />
  );
}

// Export available icon names for debugging
export const availableIcons = Object.keys(iconManifest);
